<?phprequire_once 'classes/UfoCaptcha.php';/** * Класс наследует класс UfoCaptcha и делает все подлежащие тестированию методы публичными. */class UfoCaptchaDummy extends UfoCaptcha{    public $captchaStruct = null;        public function getStorageData()    {        return parent::getStorageData();    }        public function getDataByKey($key)    {        return parent::getDataByKey($key);    }        public function saveToStorage($timestamp, $key, $value)    {        return parent::saveToStorage($timestamp, $key, $value);    }        public function clearOldStorageData()    {        return parent::clearOldStorageData();    }        public function getRandomData($strLen = 4, $strPad = '0')    {        return parent::getRandomData($strLen, $strPad);    }}